<?php    if (!defined('BASEPATH')){        exit('No direct script access allowed');    }        if (session_status() == PHP_SESSION_NONE){        session_start();    }        require_once(APPPATH . 'libraries/twig/Twig/Autoloader.php');        class Twig {        var $loader;        var $twig;                public function __construct(){            Twig_Autoloader::register();            $this->loader = new Twig_Loader_Filesystem(APPPATH . 'views');            $this->twig = new Twig_Environment($this->loader);        }                public function render($page, $data){            $template = $this->twig->loadTemplate($page);            return $template->render($data);        }    }